DROP POLICY IF EXISTS "Anyone can view events" ON events; DROP POLICY IF EXISTS "Authenticated users can create events" ON events; DROP POLICY IF EXISTS "Users can update their own events" ON events; DROP TABLE IF EXISTS events; CREATE TABLE events (id UUID DEFAULT gen_random_uuid() PRIMARY KEY, title TEXT NOT NULL, date TIMESTAMP WITH TIME ZONE NOT NULL, location TEXT, description TEXT, created_at TIMESTAMP WITH TIME ZONE DEFAULT TIMEZONE('utc'::text, NOW()) NOT NULL, user_id UUID REFERENCES auth.users(id), created_by TEXT); ALTER TABLE events ENABLE ROW LEVEL SECURITY; CREATE POLICY "Anyone can view events" ON events FOR SELECT USING (true); CREATE POLICY "Authenticated users can create events" ON events FOR INSERT WITH CHECK (true); CREATE POLICY "Users can update their own events" ON events FOR UPDATE USING (auth.uid()::text = created_by) WITH CHECK (auth.uid()::text = created_by);
